package icehs.science.chapter03;

public class StatisticsCastingTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double probability=0.0000001235;
		long people=6973738433L;
		System.out.println("로또에 당첨될 확률 : "+probability);
		System.out.println("로또에 당첨될 확률 int 변환 : "+(int)probability);
		System.out.println("전 세계의 인구 수 : "+people);
		System.out.println("전 세계의 인구 수 int 변환 : "+(int)people);

	}

}
