package icehs.science.chapter04;

public class CalculatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=20;
		int num2=7;
		
		System.out.println("오 써쎀 : "+(num1+num2));
		System.out.println("짋 써쎀 : "+(num1-num2));
		System.out.println("썼 써쎀 : "+(num1*num2));
		System.out.println("씱얋 써쎀 : "+(num1/num2));
		System.out.println("씱쟵쵔 : "+(num1%num2));

	}

}
