package icehs.science.chapter05;

public class GuGuDanTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int dan=2;dan<=9;dan++) {
			for(int num=1;num<=9;num++) {
				System.out.print(dan+" * "+num+" = "+dan*num+", ");
			}
			System.out.println();
		}

	}

}
